INSERT INTO scene_role ("scene_id", "role_id", "line") VALUES (1, 1, 'Ive got it! The message reads [INSERT MESSAGE HERE]');
INSERT INTO scene_role ("scene_id", "role_id", "line") VALUES (1, 2, 'Finally, the poindexters have solved it. Give me that message, Ive got a world to save...');
INSERT INTO scene_role ("scene_id", "role_id", "line") VALUES (2, 2, 'Looks like youre the ones who got abducted this time!');
INSERT INTO scene_role ("scene_id", "role_id", "line") VALUES (2, 2, 'Sorry, but it looks like your invasion just got cancelled!');
INSERT INTO scene_role ("scene_id", "role_id", "line") VALUES (2, 2, 'I guess you didnt come in peace!');
