INSERT INTO scene_role ("scene_id", "role_id", "line") VALUES (6, 4, 'We are on the brink of an alien invasion...');
INSERT INTO scene_role ("scene_id", "role_id", "line") VALUES (6, 3, 'How do you know?');
INSERT INTO scene_role ("scene_id", "role_id", "line") VALUES (6, 4, 'Becasue [INSERT MYSTERY ANSWER HERE]...');
INSERT INTO scene_role ("scene_id", "role_id", "line") VALUES (8, 5, 'This is what separates the heroes from the sidekicks');
INSERT INTO scene_role ("scene_id", "role_id", "line") VALUES (8, 5, 'Im not saying this is a good idea, but its definitely an idea');
INSERT INTO scene_role ("scene_id", "role_id", "line") VALUES (8, 5, 'Im about to do something stupid, but its gonna look awesome');
INSERT INTO scene_role ("scene_id", "role_id", "line") VALUES (8, 5, 'Ive never done this before, but thats never stopped me');
